          className={`text-4xl md:text-6xl font-black bg-gradient-to-r from-accent via-light to-accent bg-[length:200%_auto] text-transparent bg-clip-text leading-tight reveal ${isVisible ? 'visible' : ''}`}
          style={{ animation: isVisible ? 'text-gradient-pan 5s linear infinite' : 'none' }}
